update commission.LeadUserCommissions set FkUserId = 1098, UpdatedDate = getdate()  

from commission.LeadUserCommissions luc
inner join [commission].[CommissionItems] i on i.Id = luc.FkCommissionItemId
inner join [commission].[CommissionTypes] t on i.[FkCommissionTypeId] = t.Id
where luc.FkLeadId in (	select Id from #ConfirmPatUsers) 
and t.Id = 31 and luc.FkUserId is null



SELECT 
    luc.Id AS LeadUserCommissionId,
    luc.FkLeadId,
    luc.FkUserId AS CurrentUserId,
    i.Id AS CommissionItemId,
    t.Id AS CommissionTypeId,
    t.Name AS CommissionTypeName
FROM commission.LeadUserCommissions luc
INNER JOIN commission.CommissionItems i 
    ON i.Id = luc.FkCommissionItemId
INNER JOIN commission.CommissionTypes t 
    ON i.FkCommissionTypeId = t.Id
WHERE luc.FkLeadId IN (SELECT Id FROM #ConfirmPatUsers)
  AND t.Id = 31
  AND luc.FkUserId IS NULL;

 
DECLARE @LeadId INT;

DECLARE lead_cursor CURSOR FOR

 Select #ConfirmPatUsers.Id from #ConfirmPatUsers where Id not in (
  select FkLeadId from  quotation.LeadAssignedUsers where  FkAssignedUserId = 1098 and fkroleid = 60 )

OPEN lead_cursor;
FETCH NEXT FROM lead_cursor INTO @LeadId;

WHILE @@FETCH_STATUS = 0
BEGIN
    INSERT INTO quotation.LeadAssignedUsers (
        FkLeadId,
        FkAssignedUserId,
        FkRoleId,
        CreatedBy,
        CreatedDate,
        RStatus
    )
    VALUES (
        @LeadId,
        1098,       -- Sales Director ID
        60,       -- Role ID
        1,        -- CreatedBy (System Admin or Migration User)
        GETDATE(),
        1         -- Active
    );

    FETCH NEXT FROM lead_cursor INTO @LeadId;
END;

CLOSE lead_cursor;
DEALLOCATE lead_cursor;
